import React, { useState, useEffect } from "react";
import styled from "styled-components";
import loadable from "@loadable/component";

const Header = loadable(() => import("components/main/Header"));
const Footer = loadable(() => import("components/main/Footer"));

const Home: React.FC = () => {
  const [isToggle, setIsToggle] = useState<boolean>(false);
  const [buttonColor, setButtonColor] = useState<string>("white");

  const onClickButton = () => {
    setIsToggle(!isToggle);
  };

  useEffect(() => {
    window.alert("유리바보");
  }, []);

  useEffect(() => {
    if (isToggle) {
      setButtonColor("yellow");
    } else {
      setButtonColor("white");
    }
  }, [isToggle]);

  return (
    <Container>
      <Header />
      <div className="o-background"></div>
      <div className="wrap">
        <h1 className="h1"></h1>
        <div className="contentsShowcase">
          <article className="showcase-wrap">
            <div className="showcase-navi">
              <nav className="navigator">
                <a href="">프로젝트 의뢰</a>
                <a href="">채용정보</a>
              </nav>
            </div>
          </article>
        </div>
      </div>
      <Footer />
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 100%;
  background-color: gray;
`;

const Main = styled.div`
  width: 100%;
  flex: 1 1 auto;
  padding-top: 48px;
`;

const Button = styled.div<any>`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100px;
  height: 48px;
  background: ${(props: any) => props.buttonColor};
  border: 1px solid #dee1e6;
`;

export default Home;
