import React, { useState, useEffect } from "react";
import styled from "styled-components";
import loadable from "@loadable/component";

const Header = loadable(() => import("components/main/Header"));
const Footer = loadable(() => import("components/main/Footer"));

const Home: React.FC = () => {
  return (
    <Container>
      <Header />
      <div className="o-background"></div>
      <div className="wrap">
        <h1 className="h1"></h1>
        <div className="contentsShowcase">
          <article className="showcase-wrap">
            <div className="showcase-navi">
              <nav className="navigator">
                <a href="">프로젝트 의뢰</a>
                <a href="">채용정보</a>
              </nav>
            </div>

            <section>
              <div className="section-body">
                <a href="" className="title">
                  <span className="subtitle"></span>
                  <h2>
                    "트렌드 플랫폼으로 전환한 "
                    <br />
                    <strong>더 현대 서울</strong>
                  </h2>
                  <p className="mores">"프로젝트 보기"</p>
                </a>
              </div>

              <figure>
                <span className="mask"></span>
                <div className="o-imgLoaded">
                  <img
                    src="//cdn.dfy.co.kr/images/showcase_hyundai-department.jpg"
                    data-src="//cdn.dfy.co.kr/images/showcase_hyundai-department.jpg"
                    data-width="1920"
                    data-height="1920"
                    data-bgcolor="#dcdcdc"
                    alt="hyundai-department"
                    className="showedLoaded"
                  />
                </div>
              </figure>
            </section>
          </article>
        </div>
      </div>
      <Footer />
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 100%;
  background-color: #fff;
`;

const Main = styled.div`
  width: 100%;
  flex: 1 1 auto;
  padding-top: 48px;
`;

const Button = styled.div<any>`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100px;
  height: 48px;
  background: ${(props: any) => props.buttonColor};
  border: 1px solid #dee1e6;
`;

export default Home;
